CREATE TABLE TB_COMPANY (
    COM_CODE VARCHAR2(100) NOT NULL,
    COM_SHOP_NAME VARCHAR2(20) NOT NULL,
    COM_TYPE VARCHAR2(30) NOT NULL,
    COM_NAME VARCHAR2(20) NOT NULL,
    COM_TEL VARCHAR2(20) NOT NULL,
    USER_CBR VARCHAR2(30) NOT NULL,
    COM_ADDRESS VARCHAR2(100) NOT NULL,
    COM_MAIL VARCHAR2(50),
    COM_BANK VARCHAR2(50) NOT NULL,
    CONSTRAINT PK_TB_COMPANY PRIMARY KEY (COM_CODE)
);


CREATE TABLE TB_USERINFO (
    GID VARCHAR2(20) NOT NULL,
    COM_CODE VARCHAR2(100) NOT NULL,
    USER_PW VARCHAR2(50) NOT NULL,
    USER_NAME VARCHAR2(20) NOT NULL,
    USER_JOB VARCHAR2(20),
    USER_REGNO VARCHAR2(30) NOT NULL,
    USER_PHONE VARCHAR2(30),
    USER_EMAIL VARCHAR2(50),
    USER_ADDR VARCHAR2(100),
    USER_ENTRY_DATE DATE,
    USER_PAY NUMBER,
    USER_CONTRACT VARCHAR2(50) NOT NULL,
    USER_ACCOUNT VARCHAR2(50),
    USER_CARD VARCHAR2(50),
    USER_ROLE VARCHAR2(20) NOT NULL,
    USER_PICTURE VARCHAR2(255) NOT NULL,
    CONSTRAINT PK_TB_USERINFO PRIMARY KEY (GID, COM_CODE),
    CONSTRAINT FK_TB_USERINFO_COMCODE FOREIGN KEY (COM_CODE) REFERENCES TB_COMPANY (COM_CODE)
);

CREATE TABLE TB_LEAVE (
    LEAVE_NO NUMBER NOT NULL,
    LEAVE_CRE_DATE DATE DEFAULT SYSDATE NOT NULL,
    LEAVE_STR_DATE DATE NOT NULL,
    LEAVE_END_DATE DATE NOT NULL,
    LEAVE_ANN_TYPE VARCHAR2(20) NOT NULL,
    LEAVE_STATUS VARCHAR2(20),
    LEAVE_COMMENT VARCHAR2(100),
    GID VARCHAR2(20) NOT NULL,
    COM_CODE VARCHAR2(100) NOT NULL,
    CONSTRAINT PK_TB_LEAVE PRIMARY KEY (LEAVE_NO),
    CONSTRAINT FK_TB_LEAVE_USERINFO FOREIGN KEY (GID, COM_CODE) REFERENCES TB_USERINFO (GID, COM_CODE)
);

CREATE TABLE TB_WORKER (
    WORK_NO NUMBER NOT NULL,
    WORK_AREA VARCHAR2(50) NOT NULL,
    WORK_PROJECT VARCHAR2(50) NOT NULL,
    WORK_COMMENT VARCHAR2(100),
    WORK_TIME NUMBER NOT NULL,
    GID VARCHAR2(20) NOT NULL,
    COM_CODE VARCHAR2(100) NOT NULL,
    CONSTRAINT PK_TB_WORKER PRIMARY KEY (WORK_NO),
    CONSTRAINT FK_TB_WORKER_USERINFO FOREIGN KEY (GID, COM_CODE) REFERENCES TB_USERINFO (GID, COM_CODE)
);

CREATE TABLE TB_SALARY (
    SALARY_NO NUMBER NOT NULL,
    GID VARCHAR2(20) NOT NULL,
    SALARY_INCOMET NUMBER,
    SALARY_LOCALT NUMBER,
    SALARY_NPS NUMBER,
    SALARY_HI NUMBER,
    SALARY_EI NUMBER,
    SALARY_WCI NUMBER,
    SALARY_LTCI NUMBER,
    SALARY_TOTAL NUMBER,
    SALARY_PAYDATE DATE,
    SALARY_INCENTIVE NUMBER,
    SALARY_PAY NUMBER,
    ACCOUNT_NUMBER VARCHAR2(20),
    COM_CODE VARCHAR2(100) NOT NULL,
    CONSTRAINT PK_TB_SALARY PRIMARY KEY (SALARY_PAYDATE),
    CONSTRAINT FK_TB_SALARY_USERINFO FOREIGN KEY (GID, COM_CODE) REFERENCES TB_USERINFO (GID, COM_CODE)
);

CREATE TABLE TB_RETIRE (
    RET_NO NUMBER NOT NULL,
    COM_CODE VARCHAR2(100) NOT NULL,
    RET_TYPE VARCHAR2(20) NOT NULL,
    RET_EXITREASON VARCHAR2(300) NOT NULL,
    RET_DATE DATE NOT NULL,
    RET_TOTAL_DATE DATE NOT NULL,
    CONSTRAINT PK_TB_RETIRE PRIMARY KEY (RET_NO, COM_CODE),
    CONSTRAINT FK_TB_RETIRE_COMPANY FOREIGN KEY (COM_CODE) REFERENCES TB_COMPANY (COM_CODE)
);

CREATE TABLE TB_RETIRE_PAY (
    RP_NO VARCHAR2(255) NOT NULL,
    RP_NN NUMBER,
    RP_POLICY VARCHAR2(8),
    RP_INCOME_TAX NUMBER,
    RP_LOCAL_INCOME_TAX NUMBER,
    RP_PAYMENT NUMBER,
    RP_TYPE VARCHAR2(20),
    RP_DUE_DATE DATE,
    RP_STATUS CHAR(1),
    RET_NO NUMBER NOT NULL,
    COM_CODE VARCHAR2(100) NOT NULL,
    CONSTRAINT PK_TB_RETIRE_PAY PRIMARY KEY (RP_NO),
    CONSTRAINT FK_TB_RETIRE_PAY_RETIRE FOREIGN KEY (RET_NO, COM_CODE) REFERENCES TB_RETIRE (RET_NO, COM_CODE)
);

CREATE TABLE TB_PRODUCT (
    PRODUCT_CD VARCHAR2(20) NOT NULL,
    COM_CODE VARCHAR2(100) NOT NULL,
    PRODUCT_NAME VARCHAR2(20) NOT NULL,
    PRODUCT_SIZE VARCHAR2(50),
    PRODUCT_PRICE NUMBER NOT NULL,
    PRODUCT_PURCHASE_PRICE NUMBER NOT NULL,
    CONSTRAINT PK_TB_PRODUCT PRIMARY KEY (PRODUCT_CD, COM_CODE),
    CONSTRAINT FK_TB_COMPANY_PRODUCT FOREIGN KEY (COM_CODE) REFERENCES TB_COMPANY (COM_CODE)
);

CREATE TABLE TB_ERP_CLIENT (
    CLIENT_CODE VARCHAR2(50) NOT NULL,
    COM_CODE VARCHAR2(100) NOT NULL,
    CLIENT_NAME VARCHAR2(30) NOT NULL,
    CLIENT_TYPE VARCHAR2(30),
    CLIENT_NO NUMBER NOT NULL,
    CLIENT_TEL VARCHAR2(30) NOT NULL,
    CLIENT_ADDR VARCHAR2(100) NOT NULL,
    CLIENT_EMAIL VARCHAR2(50),
    CLIENT_FAX VARCHAR2(30),
    CONSTRAINT PK_TB_ERP_CLIENT PRIMARY KEY (CLIENT_CODE, COM_CODE),
    CONSTRAINT FK_TB_COMPANY_ERPCLIENT FOREIGN KEY (COM_CODE) REFERENCES TB_COMPANY (COM_CODE)
);

CREATE TABLE TB_PODER (
    PORDER_CD NUMBER NOT NULL,
    PORDER_DATE DATE NOT NULL,
    DUE_DATE DATE NOT NULL,
    PORDER_CNT NUMBER NOT NULL,
    PORDER_TAX NUMBER,
    PORDER_SUM NUMBER NOT NULL,
    EMP_NAME VARCHAR2(20) NOT NULL,
    FIELD_NUM VARCHAR2(20),
    PRODUCT_CD VARCHAR2(20) NOT NULL,
    COM_CODE VARCHAR2(100) NOT NULL,
    CLIENT_CODE VARCHAR2(50) NOT NULL,
    COM_CODE2 VARCHAR2(100) NOT NULL,
    CONSTRAINT PK_TB_PODER PRIMARY KEY (PORDER_CD),
    CONSTRAINT FK_TB_PODER_PRODUCT FOREIGN KEY (PRODUCT_CD, COM_CODE) REFERENCES TB_PRODUCT (PRODUCT_CD, COM_CODE),
    CONSTRAINT FK_TB_PODER_ERPCLIENT FOREIGN KEY (CLIENT_CODE, COM_CODE2) REFERENCES TB_ERP_CLIENT (CLIENT_CODE, COM_CODE)
);

CREATE TABLE TB_INVENTORY (
    STOCK_THRESHOLD NUMBER,
    CURRENT_STOCK NUMBER,
    PRODUCT_CD VARCHAR2(20) NOT NULL,
    COM_CODE VARCHAR2(100) NOT NULL,
    CONSTRAINT FK_TB_INVENTORY_COMPANY FOREIGN KEY (COM_CODE) REFERENCES TB_COMPANY (COM_CODE)
);

CREATE TABLE TB_ORDER (
    ORDER_CD NUMBER NOT NULL,
    ORDER_TYPE VARCHAR2(20) NOT NULL,
    ORDER_DATE DATE NOT NULL,
    ORDER_RECIEVE_DATE DATE NOT NULL,
    ORDER_AMOUNT NUMBER NOT NULL,
    ORDER_TAX NUMBER,
    ORDER_PAYMENT_YN CHAR(1) NOT NULL,
    ORDER_CANCEL_YN CHAR(1) NOT NULL,
    PRODUCT_CD VARCHAR2(20) NOT NULL,
    COM_CODE VARCHAR2(100) NOT NULL,
    CONSTRAINT PK_TB_ORDER PRIMARY KEY (ORDER_CD),
    CONSTRAINT FK_TB_ORDER_PRODUCT FOREIGN KEY (PRODUCT_CD, COM_CODE) REFERENCES TB_PRODUCT (PRODUCT_CD, COM_CODE)
);

CREATE TABLE TB_REF (
    ORDER_CD NUMBER NOT NULL,
    PORDER_CD NUMBER NOT NULL,
    CONSTRAINT PK_TB_REF PRIMARY KEY (ORDER_CD, PORDER_CD),
    CONSTRAINT FK_TB_REF_ORDER FOREIGN KEY (ORDER_CD) REFERENCES TB_ORDER (ORDER_CD),
    CONSTRAINT FK_TB_REF_PODER FOREIGN KEY (PORDER_CD) REFERENCES TB_PODER (PORDER_CD)
);

CREATE TABLE TB_CHECK (
    ORDER_CD NUMBER NOT NULL,
    EXCHANGE_YN CHAR(1),
    EXCHANGE_DATE DATE,
    CONSTRAINT PK_TB_CHECK PRIMARY KEY (ORDER_CD),
    CONSTRAINT FK_TB_CHECK_ORDER FOREIGN KEY (ORDER_CD) REFERENCES TB_ORDER (ORDER_CD)
);

CREATE TABLE TB_REFUND (
    ORDER_CD NUMBER NOT NULL,
    REFUND_DATE DATE,
    REFUND_REASON VARCHAR2(300),
    CONSTRAINT PK_TB_REFUND PRIMARY KEY (ORDER_CD),
    CONSTRAINT FK_TB_REFUND_CHECK FOREIGN KEY (ORDER_CD) REFERENCES TB_CHECK (ORDER_CD)
);

CREATE TABLE TB_EXCHANGE (
    ORDER_CD NUMBER NOT NULL,
    COM_CODE VARCHAR2(100) NOT NULL,
    EXC_DATE DATE,
    EXC_REASON VARCHAR2(300),
    CONSTRAINT PK_TB_EXCHANGE PRIMARY KEY (ORDER_CD),
    CONSTRAINT FK_TB_EXCHANGE_CHECK FOREIGN KEY (ORDER_CD) REFERENCES TB_CHECK (ORDER_CD)
);

CREATE TABLE TB_TOTAL (
    MONTH_NO NUMBER NOT NULL,
    COM_CODE VARCHAR2(100) NOT NULL,
    MONTH_SALARY NUMBER,
    MONTH_PROFIT NUMBER,
    MONTH_EXPENSES NUMBER,
    CONSTRAINT PK_TB_TOTAL PRIMARY KEY (MONTH_NO, COM_CODE),
    CONSTRAINT FK_TB_TOTAL_COMPANY FOREIGN KEY (COM_CODE) REFERENCES TB_COMPANY (COM_CODE)
);

CREATE TABLE TB_GOOUTWORK (
    GOOUTWORK_DATE DATE NOT NULL,
    GOOUTWORK_GOD DATE,
    GOOUTWORK_OUTD DATE,
    GOOUTWORK_TIME DATE,
    GID VARCHAR2(20) NOT NULL,
    COM_CODE VARCHAR2(100) NOT NULL,
    CONSTRAINT PK_TB_GOOUTWORK PRIMARY KEY (GOOUTWORK_DATE),
    CONSTRAINT FK_TB_GOOUTWORK_USERINFO FOREIGN KEY (GID, COM_CODE) REFERENCES TB_USERINFO (GID, COM_CODE)
);


