mapper.select:
select *
from  tb_gooutwork
where gooutwork_date = to_date(sysdate,'yy-MM-dd') and gid = #{ gId } and COM_CODE = #{comCode}

select DISTINCT gid
from tb_gooutwork
where COM_CODE = #{comCode}

mapper.update:
update tb_gooutwork
set GOOUTWORK_god = #{goDate}
where gid = #{ gId } and com_code = #{ comCode } and  to_date(gooutwork_date,'yy-MM-dd') = to_date(sysdate,'yy-MM-dd')

update tb_gooutwork
set
GOOUTWORK_GOD = #{goDate},
GOOUTWORK_OUTD = #{outDate},
GOOUTWORK_TIME = #{time}
where gid = #{ gId } and GOOUTWORK_DATE = #{day} and COM_CODE = #{comCode}
